import React, { useState, useEffect } from 'react'
import { Row, Col, Card, CardHeader, CardBody, 
    Form, FormGroup, FormText, Label, Input, Alert,
    Button, 
    CardFooter} from 'reactstrap'
import axios from 'axios'

const Settings = () => {

    const [account, setAccount] = useState({})
    const [alertDisplay, setAlertDisplay] = useState(false)
    const [alertColor, setAlertColor] = useState('')
    const [alertMsg, setAlertMsg] = useState('')

    const onChangeUsername = (event) => {
        setAccount({ ...account, username: event.target.value})
    }

    const onChangePassword = (event) => {
        setAccount({ ...account, password: event.target.value})
    }

    const onChangeConfirmPassword = (event) => {
        setAccount({ ...account, confirmPassword: event.target.value})
    }

    const onChangeFirstName = (event) => {
        setAccount({ ...account, firstName: event.target.value})
    }

    const onChangeLastName = (event) => {
        setAccount({ ...account, lastName: event.target.value})
    }

    useEffect(() => {
        const fetchData = async () => {
            const response = await axios({
                url: 'http://localhost:5000/api/account',
                method: 'GET',
                headers: {
                    'Accept': 'application/json',
                    'Content-type': 'application/json'
                },
                data: {

                }
            })
            setAccount(response.account)
        }
        fetchData()
    })

    const onSubmit = (event) => {

    }

    return (
        <Col xs={{ size: 12 }} sm={{ size: 6, offset: 3 }}>
            <Card>
                <CardHeader>
                    <h3 style={{ textAlign: 'center' }}>Settings</h3>
                </CardHeader>
                <CardBody>
                    <Form>
                        <Row>
                            <Col xs={12}>
                                <Alert isOpen={alertDisplay} color={alertColor}>{alertMsg}</Alert>
                                <FormGroup>
                                    <Label for="username">Username</Label>
                                    <Input type="text" id="username" value={account.username} 
                                        onChange={onChangeUsername} />
                                </FormGroup>
                            </Col>
                        </Row>
                        <Row form>
                            <Col xs={12} sm={6}>
                                <FormGroup>
                                    <Label for="password">Password</Label>
                                    <Input type="password" id="password" value={account.password}
                                        onChange={onChangePassword} />
                                </FormGroup>
                            </Col>
                            <Col xs={12} sm={6}>
                                <FormGroup>
                                    <Label for="confirm-password">Confirm Password</Label>
                                    <Input type="password" id="confirm-password" value={account.confirmPassword}
                                        onChange={onChangeConfirmPassword} />
                                </FormGroup>
                            </Col>
                        </Row>
                        <Row form>
                            <Col xs={12} sm={6}>
                                <FormGroup>
                                    <Label for="first-name">First Name</Label>
                                    <Input type="text" id="first-name" value={account.firstName}
                                        onChange={onChangeFirstName} />
                                </FormGroup>
                            </Col>
                            <Col xs={12} sm={6}>
                                <FormGroup>
                                    <Label for="last-name">Last Name</Label>
                                    <Input type="text" id="last-name" value={account.lastName}
                                        onChange={onChangeLastName} />
                                </FormGroup>
                            </Col>
                        </Row>
                        <Row>
                            <Col xs={12}>
                                <FormGroup check>
                                    <Label check>
                                        <Input type="checkbox" />{' '}
                                        Administrator
                                    </Label>
                                </FormGroup>
                            </Col>
                        </Row>
                    </Form>
                </CardBody>
                <CardFooter>
                    <Button className="col-12 col-sm-4 offset-sm-4" onClick={onSubmit}>Update</Button>
                </CardFooter>
            </Card>
        </Col>
    )
}

export default Settings
