import { GET_TASKS, ADD_TASK, DELETE_TASK } from '../actions/types'

const initialState = {
    tasks: [{
        id: 1,
        title: 'test',
        category: 'test',
        description: 'test',
        important: true,
        date: '10/23/20'
    }]
}

export default function(state = initialState, action) {
    switch(action.type) {
        case GET_TASKS:
            return {
                ...state
            }
        case ADD_TASK:
            return {
                ...state,
                tasks: [...state.tasks, action.payload]
            }
        case DELETE_TASK:
            return {
                ...state,
                tasks: state.tasks.filter(task => task.id !== action.payload)
            }
        default:
            return {
                ...state
            }
    }
}
