import React, { useState } from 'react'
import { Row, Col, Form, FormGroup, Label,
    Input, Alert, Button } from 'reactstrap'

export default function Create(props) {

    // Array destructuring
    const [title, setTitle] = useState('')
    const [description, setDescription] = useState('')
    const [alertColor, setAlertColor] = useState('')
    const [alertMsg, setAlertMsg] = useState('')

    function onChangeTitle(event) {
        console.log(event)
        setTitle(event.target.value)
    }

    function onChangeDescription(event) {
        setDescription(event.target.value)
    }

    function onSubmit(event) {

    }

    return (
        <Row>
            <Col sm={{ size: 6, offset: 3 }} xs={{ size: 12 }}>
                <h3 style={{ textAlign: 'center' }}>Create a Task</h3>
                <Form>
                    <Alert color={alertColor}>{alertMsg}</Alert>
                    <FormGroup>
                        <Label for="title">Title</Label>
                        <Input type="email" id="title" value={title} 
                            onChange={() => onChangeTitle()}/>
                    </FormGroup>
                    <FormGroup>
                        <Label for="description">Text Area</Label>
                        <Input type="textarea" id="description" value={description}
                            onChange={() => onChangeDescription()}/>
                    </FormGroup>
                    <Button onClick={onSubmit()}>Submit</Button>
                </Form>
            </Col>
        </Row>
    )
}
