import React, { useState } from 'react'
import PropTypes from 'prop-types'
import { Button, Input } from 'reactstrap'

export default function Task(props) {

    const [toggleEdit, setToggleEdit] = useState(false)
    const [title, setTitle] = useState(props.task.title)
    const [category, setCategory] = useState(props.task.category)
    const [important, setImportant] = useState(props.task.category)

    const onChangeTitle = (event) => {
        setTitle(event.target.value)
    }

    const onChangeCategory = (event) => {
        setCategory(event.target.value)
    }

    const onChangeImportant = (event) => {
        setImportant(event.target.value)
    }

    const onClick = (event) => {

    }

    if(toggleEdit)
        return (
            <tr>
                <td>{props.task.id}</td>
                <td>{props.task.title}</td>
                <td>{props.task.category}</td>
                <td>{props.task.date}</td>
                <td>{props.task.time}</td>
                <td>{props.task.important}</td>
                <td>
                    <Button outline={(toggleEdit) ? 'true': 'false'}>Edit</Button>
                </td>
            </tr>
        )
    else
        return (
            <tr>
                <td>{props.task.id}</td>
                <td>
                    <Input type="text" value={title}
                        onChange={onChangeTitle} />
                </td>
                <td>
                    <Input type="select" value={category}
                        onChange={onChangeCategory} />
                        <option hidden selected>Select</option>
                        <option>Printing</option>
                        <option>Updating</option>
                        <option>Networking/Internet</option>
                        <option>Other</option>
                </td>
                <td>{props.task.date}</td>
                <td>{props.task.time}</td>
                <td>
                    <Input type="checkbox" check={important ? 'true' : 'false'}
                        onChange={onChangeImportant} />
                </td>
                <td>
                    <Button outline={(toggleEdit) ? 'true': 'false'}>Edit</Button>
                </td>
            </tr>
        )

}

Task.propTypes = {
    task: PropTypes.object.isRequired
}
