import React, { useState, useEffect } from 'react'
import { Col, Card, CardHeader, CardBody, 
    Form, FormGroup, FormText, Label, Input, Alert,
    Button, 
    CardFooter} from 'reactstrap'
import axios from 'axios'

const Settings = () => {

    const [account, setAccount] = useState({})
    const [alertDisplay, setAlertDisplay] = useState(false)
    const [alertColor, setAlertColor] = useState('')
    const [alertMsg, setAlertMsg] = useState('')

    useEffect(async () => {
        const response = await axios({
			url: 'http://localhost:5000/api/account',
			method: 'GET',
			headers: {
				'Accept': 'application/json',
				'Content-type': 'application/json'
            },
            data: {

            }
        })
        setAccount(response.account)
    })

    onSubmit = () => {

    }

    return (
        <Col xs={{ size: 12 }} sm={{ size: 6, offset: 3 }}>
            <Card>
                <CardHeader>
                    <h3 style={{ textAlign: 'center' }}>Settings</h3>
                </CardHeader>
                <CardBody>
                    <Form>
                        <Alert isOpen={alertDisplay} color={alertColor}>{alertMsg}</Alert>
                    </Form>
                </CardBody>
                <CardFooter>
                    <Button className="col-12 col-sm-4 offset-sm-4" onClick={onSubmit}>Submit</Button>
                </CardFooter>
            </Card>
        </Col>
    )
}

export default Settings
