import React, { useState } from 'react'
import { Row, Col, Form, FormGroup, Label,
    Input, Alert, Button, CardHeader, CardBody } from 'reactstrap'

export default function Create(props) {

    // Array destructuring
    const [title, setTitle] = useState('')
    const [description, setDescription] = useState('')
    const [alertDisplay, setAlertDisplay] = useState(false)
    const [alertColor, setAlertColor] = useState('')
    const [alertMsg, setAlertMsg] = useState('')

    const onChangeTitle = (event) => {
        setTitle(event.target.value)
    }

    const onChangeDescription = (event) => {
        setDescription(event.target.value)
    }

    const onSubmit = (event) => {

    }

    return (
        <Row>
            <Col xs={{ size: 12 }} >
                <Card>
                    <CardHeader>

                    </CardHeader>
                    <CardBody>

                    </CardBody>
                </Card>
                <Form>
                    <Col xs={{ size: 12 }} sm={{ size: 6, offset: 3 }}>
                        <h3 style={{ textAlign: 'center' }}>Create a Task</h3>
                        <Alert isOpen={alertDisplay} color={alertColor}>{alertMsg}</Alert>
                        <FormGroup>
                            <Label for="title">Title</Label>
                            <Input type="text" value={title} 
                                onChange={onChangeTitle}/>
                        </FormGroup>
                        <FormGroup>
                            <Label for="description">Text Area</Label>
                            <Input type="textarea" value={description}
                                onChange={onChangeDescription}/>
                        </FormGroup>
                    </Col>
                    <Col xs={{ size: 12 }} sm={{ size: 4, offset: 4 }}>
                        <Button onClick={onSubmit}>Submit</Button>
                    </Col>
                </Form>
            </Col>
        </Row>
    )
}
