import React, { useState } from 'react'
import { Col, Card, CardHeader, CardBody, 
    Form, FormGroup, FormText, Label, Input, Alert,
    Button, 
    CardFooter} from 'reactstrap'

const Create = (props) => {

    // Array destructuring
    const [task, setTask] = useState({
        title: '',
        category: '',
        description: '',
        important: false,
        file: {}
    })
    const [alert, setAlert] = useState({
        msg: '',
        display: '',
        color: ''
    })

    const onChangeTitle = (event) => {
        setTask({ ...task, title: event.target.value })
    }

    const onChangeCategory = (event) => {
        setTask({ ...task, category: event.target.value })
    }

    const onChangeDescription = (event) => {
        setTask({ ...task, description: event.target.value })
    }

    const onChangeImportant = (event) => {
        setTask({ ...task, important: event.target.checked })
    }

    const onChangeFile = (event) => {
        setTask({ ...task, file: event.target.value })
    }

    const onSubmit = (event) => {
        if(task.some(field => field)) {
            setAlert({ ...alert, msg: 'Please fill out the entire form' })
        }

    }

    return (
        <Col xs={{ size: 12 }} sm={{ size: 6, offset: 3 }}>
            <Card>
                <CardHeader>
                    <h3 style={{ textAlign: 'center' }}>Create a Task</h3>
                </CardHeader>
                <CardBody>
                    <Form>
                        <Alert isOpen={alert.display} color={alert.color}>{alert.msg}</Alert>
                        <FormGroup>
                            <Label for="title">Title</Label>
                            <Input type="text" id="title" value={task.title}
                                onChange={onChangeTitle} />
                        </FormGroup>
                        <FormGroup>
                            <Label for="category">Category</Label>
                            <Input type="select" id="category" value={task.category}
                                onChange={onChangeCategory}>
                                <option hidden selected>Select</option>
                                <option>Printing</option>
                                <option>Updating</option>
                                <option>Networking/Internet</option>
                                <option>Other</option>
                            </Input>
                        </FormGroup>
                        <FormGroup>
                            <Label for="description">Description</Label>
                            <Input type="textarea" id="description" value={task.description}
                                onChange={onChangeDescription} />
                        </FormGroup>
                        <FormGroup>
                            <Label for="file">Image</Label>
                            <Input type="file" id="file" value={task.file}
                                onChange={onChangeFile} /> 
                            <FormText color="muted">
                            This is optional
                            </FormText>      
                        </FormGroup>
                        <FormGroup check>
                            <Label check>
                                <Input type="checkbox" value={task.important}
                                    onChange={onChangeImportant} />{' '}
                                Important
                            </Label>
                        </FormGroup>
                    </Form>
                </CardBody>
                <CardFooter>
                    <Button className="col-12 col-sm-4 offset-sm-4" onClick={onSubmit}>Submit</Button>
                </CardFooter>
            </Card>
        </Col>
    )
}

export default Create
