import React, { useState } from 'react'
import { Row, Col, Card, CardHeader, CardBody, 
    Form, FormGroup, Label, Input, Alert,
    Button } from 'reactstrap'

export default function Create(props) {

    // Array destructuring
    const [title, setTitle] = useState('')
    const [description, setDescription] = useState('')
    const [important, setImportant] = useState(false)
    const [alertDisplay, setAlertDisplay] = useState(false)
    const [alertColor, setAlertColor] = useState('')
    const [alertMsg, setAlertMsg] = useState('')

    const onChangeTitle = (event) => {
        setTitle(event.target.value)
    }

    const onChangeDescription = (event) => {
        setDescription(event.target.value)
    }

    const onChangeImportant = (event) => {
        setImportant(event.target.value)
    }

    const onSubmit = (event) => {

    }

    return (
        <Row>
            <Col xs={{ size: 12 }} sm={{ size: 6, offset: 3 }}>
                <Card>
                    <CardHeader>
                        <h3 style={{ textAlign: 'center' }}>Create a Task</h3>
                    </CardHeader>
                    <CardBody>
                        <Form>
                            <Alert isOpen={alertDisplay} color={alertColor}>{alertMsg}</Alert>
                            <FormGroup>
                                <Label for="title">Title</Label>
                                <Input type="text" id="title"
                                    value={title} onChange={onChangeTitle}/>
                            </FormGroup>
                            <FormGroup>
                                <Label for="description">Text Area</Label>
                                <Input type="textarea" id="description"
                                    value={description} onChange={onChangeDescription}/>
                            </FormGroup>
                            <FormGroup>
                                <Label for="important">Important</Label>
                                <Input type="checkbox" id="important"
                                    value={important} onChange={onChangeImportant}/>
                            </FormGroup>
                            <Button onClick={onSubmit}>Submit</Button>
                        </Form>
                    </CardBody>
                </Card>
            </Col>
        </Row>
    )
}
